package ExerciciosPortoSeguroDHouse;

import java.util.Scanner;

public class Aula02Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * João Papo-de-Pescador, homem de bem, comprou um microcomputador 
		 * para controlar o rendimento diário de seu trabalho. Toda vez que 
		 * ele traz um peso de tomate maior que o estabelecido pelo regulamento 
		 * do estado de São Paulo (50 quilos) deve pagar um multa de R$ 4,00 por
		 * quilo excedente. João precisa que você faça um sistema que leia a
		 * variável P (peso de tomates) e verifique se há excesso. Se houver, 
		 * gravar na variável E (Excesso) e na variável M o valor da multa que
		 * João deverá pagar. Caso contrário mostrar tais variáveis com o 
		 * conteúdo ZERO.
		 */
		
		final int pesoMax = 50;
		final float taxaMulta = 4.00f;
		
		float peso = 0, excesso = 0, multa = 0;
		
		@SuppressWarnings("resource")
		Scanner entrada = new Scanner(System.in);
		
		System.out.println("Entre com o peso: ");
		peso = entrada.nextFloat();		
		
		if(peso > pesoMax) {
			excesso = peso - pesoMax;
			multa = excesso * taxaMulta;
			System.out.printf("\n Seu peso é %.2f kg, excesso: %.2f kg e multa: R$ %.2f.",peso,excesso,multa);
			
		}
		else {
			System.out.printf("\n Seu peso é %.2f kg, excesso: %.2f kg e multa: R$ %.2f.",peso,excesso,multa);
			
		}

	}

}
